// нужен чисто чтобы у каждого элемента был свой цвет
// индивидуальность для каждой расы

import ReactDOM from 'react-dom';
import React from "react";
import './point.css'

class Point extends React.Component {
    constructor(props) {
        super(props)
        this.onClick = this.onClick.bind(this)
        this.state = {
            pos: 0,
            className: "is-hidden"
        }

    }
    render() {
        return (
            <div className={"race-list-li " + this.state.className} onClick={this.onClick}>
                {this.props.children}
            </div>
        );
    }

    setIsHidden() {
        this.setState({
            className: "is-hidden"
        })
    }

    setShowedAdd() {
        // this.setState({
        //     className: "showed-add"
        // })
        alert('!')
    }

    setShowedRemove() {
        this.setState({
            className: "showed-remove"
        })
    }
}

export default Point
