// нужен чисто чтобы у каждого элемента был свой цвет
// индивидуальность для каждой расы

import ReactDOM from 'react-dom';
import React from "react";
import './li.css'
import Point from "./point/point";

class Li extends React.Component {
    constructor(props) {
        super(props)
        this.onClick = this.onClick.bind(this)
        this.myRef = React.createRef()
        this.state = {
            pos: 0
        }
    }
    render() {
        return (
            <div className="race-list-li" id={"race-list-li"+this.props.children} onClick={this.onClick}>
                <Point ref={this.myRef}/>
                {this.props.children}
            </div>
        );
    }

    onClick() {
        this.myRef.current.setShowedAdd()
    }
}

export default Li
