import React from "react";

class Timer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            seconds: 0
        }
    }

    tick() {
        this.setState(state => (
            {
                seconds: state.seconds + 1
            }
        ))
    }

    componentDidMount() {
        this.interval = setInterval(
            () => this.tick(),
            1
        )
    }

    componentWillUnmount() {
        clearInterval(this.interval)
    }

    render() {
        return (
            <div>
                seconds: {this.state.seconds}
            </div>
        )
    }
}

export default Timer
