// нужен чисто чтобы у каждого элемента был свой цвет
// индивидуальность для каждой расы

import ReactDOM from 'react-dom';
import React from "react";
import './li.css'

class Li extends React.Component {
    constructor(props) {
        super(props)
        this.onClick = this.onClick.bind(this)
        this.state = {
            pos: 0
        }
    }
    render() {
        return (
            <div className="race-list-li" id={"race-list-li"+this.props.children} onClick={this.onClick()}>
                {this.props.children}
            </div>
        );
    }

    onClick() {
        ReactDOM.render(
            <div className="div-pick-race"/>,
            document.getElementById("race-list-li"+this.props.children)

        )
    }
}

export default Li
